import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { ProdutosService } from 'src/app/services/produtos.service';
import { Produto } from 'src/app/models/Produto';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css']
})
export class ProdutosComponent  implements OnInit{
  formulario: any;
  tituloFormulario: string = '';
  constructor(private produtoService: ProdutosService) { }
  ngOnInit(): void {
    this.tituloFormulario = 'Novo Produto';
    this.formulario = new FormGroup({
      nome: new FormControl(null),
      preco: new FormControl(null),
      categoria: new FormControl(null)
    })
  }

  enviarFormulario(): void {
    const produto: Produto = this.formulario.value;
    const observer: Observer<Produto> = {
      next(result): void {
        alert('Produto salvo com sucesso.');
      },
      error(_error): void {
        alert('Erro ao salvar!');
      },
      complete(): void {
      },
    };
    /*
    if (????) {
      this.carrosService.alterar(carro).subscribe(observer);
    } else {
    */
    this.produtoService.cadastrar(produto).subscribe(observer);
  }
}
<div class="container p-5">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow" *ngIf="formulario">
      <h6>{{ tituloFormulario }}</h6>
      <form [formGroup]="formulario" (ngSubmit)="enviarFormulario()">
        <div class="form-group">
          <label>Nome</label>
          <input type="text" class="form-control form-control-sm" formControlName="nome" />
        </div>
        <div class="form-group">
          <label>Preço</label>
          <input type="number" class="form-control form-control-sm" formControlName="preco" />
        </div>
        <div class="form-group">
          <label>Categoria</label>
          <input type="text" class="form-control form-control-sm" formControlName="categoria" />
        </div>
        <div class="container">
          <div class="row">
            <div>
              <button type="submit" class="btn btn-sm btn-outline-secondary">Salvar</button>
            </div>
            <div class="pl-1">
              <button type="button" class="btn btn-sm btn-light">Voltar</button>
            </div>
          </div>
        </div>
      </form>
    </div>
    </div>
  </div>
